// tests that the bundle generated by parcel via `npm run bundle` works

const assert = require("assert");
const UrlPattern = require("./dist/url-pattern.js").default;

const pattern = new UrlPattern("/api/users/:id");

assert.deepEqual(pattern.match("/api/users/5"), {id: "5"});
assert.deepEqual(pattern.stringify({id: 10}), "/api/users/10");
console.log("OK");
